mapper.select:
    select
     count(*)
       from
        jy_shiping_order
         where 1=1
         and custom_name=#{param1}

		 and receipt=#{param2}

		 and shiping_order_num=#{param3}

		 and close_recheck=#{param4}

    select
       *
       from
        jy_shiping_order
        where 1=1
         and custom_name=#{param3}

		 and receipt=#{param4}

		 and shiping_order_num=#{param5}

		 and close_recheck=#{param6}

		limit
		#{param1},#{param2}
