SELECT ji.shiping_order_id, ji.shiping_order_num, ji.goods_name, ji.goods_weight, ji.goods_vol, ji.goods_num, ji.order_time, CASE WHEN ji.send_type=0 THEN "自提" WHEN ji.send_type!=0 AND jca.agreement_type=1 THEN "配送" WHEN ji.send_type!=0 AND jca.agreement_type=2 THEN "转运" ELSE "送货" END AS send_type , CASE WHEN ji.shipping_order_state=0 THEN '未出库' WHEN ji.shipping_order_state=1 THEN '配送中' WHEN ji.shipping_order_state=2 THEN '转运中' WHEN ji.shipping_order_state=3 THEN '到达' WHEN ji.shipping_order_state=4 THEN '签收' END AS shipping_order_state, jso.sign_time FROM jy_shiping_order ji LEFT JOIN jy_agreementtoorder ja ON ji.shiping_order_id=ja.order_id LEFT JOIN jy_create_agreement jca ON ja.agreement_id=jca.agreement_id LEFT JOIN jy_sign_order jso ON jso.order_id=ji.shiping_order_id WHERE 1=1
and ji.send_type=0
and jca.agreement_type=1
and jca.agreement_type=2
and ji.shipping_order_state=0
and ji.shiping_order_num like "%${param4}%"
and ji.order_time >=#{param5}
and ji.order_time <=#{param6}
and ji.shiping_order_id IN
#{item}
and ji.shipping_order_state =#{param8}
ORDER BY ji.updatetime DESC
limit #{param1},#{param2}

