INNER JOIN jy_agreementtoorder jao ON jao.order_id=ji.shiping_order_id LEFT JOIN jy_create_agreement jca ON jca.agreement_id=jao.agreement_id AND jca.agreement_type=2
LEFT JOIN ${param6} jsj ON ji.shiping_order_id=jsj.order_id AND jsj.settl_order=${param7} AND jsj.settl_state=2 WHERE ${param10}
and ji.shiping_order_num like "%${param3}%"
and jsj.settl_times >=#{param4}
and jsj.settl_times <=#{param5}
and ji.shiping_order_id IN
#{item}
GROUP BY ji.shiping_order_id ORDER BY jsj.settl_times DESC, ji.updatetime DESC
limit #{param1},#{param2}

