SELECT ji.agreement_id, ji.agreement_number, ji.end_address, ji.send_station, ji.car_number1, MAX(jsj.settl_times) AS settlyf_time, CASE WHEN ji.agreement_type=1 THEN '配送' END AS agreement_types, ji.send_date, IF( jsj.settl_money IS NULL, 0, SUM(jsj.settl_money) ) AS receivable_fee, IF( jsj.settl_money IS NULL, ji.all_money, jsj.over_money + SUM(jsj.settl_money) ) AS all_money, IF( jsj.settl_money IS NULL, ji.all_money, jsj.over_money ) AS trade_cha FROM jy_create_agreement ji LEFT JOIN jy_delivery_clearing jsj ON ji.agreement_id = jsj.order_id AND jsj.settl_order = 0 AND jsj.settl_state = 2 WHERE ji.agreement_type=1
