mapper.select:
SELECT jc.company_id, sum(jso.change_fee) AS change_fee, SUM(jso.paid_chayi) AS paid_chayi, SUM(jso.change_pay) AS change_pay, SUM(jso.change_fee1) AS change_fee1 , SUM(jso.change_other) AS change_other , jca.create_time, jso.*,jc.*,jca.* from jy_shiping_order jso left join jy_agreementtoorder ja on jso.shiping_order_id = ja.order_id left join jy_create_agreement jca ON jca.agreement_id=ja.agreement_id left join jy_company jc on jca.company_id = jc.company_id where 1=1 and jca.agreement_type=2 AND jso.change_write_off!='3' AND jso.change_write_off !=0
and jso.shiping_order_num like "%${param3}%"
and jso.receipt like "%${param4}%"
and jso.fee_date >=#{param5}
and jso.fee_date <=#{param6}
and jca.create_time >=#{param7}
and lca.create_time <=#{param8}
and jca.create_time >=#{param7} and jca.create_time <=#{param8}
and jso.write_off like "%${param9}%"
and jso.send_station like "%${param10}%"
and jso.goods_name like "%${param11}%"
and jso.goods_num like "%${param12}%"
and jso.pay_type like "%${param13}%"
and jso.paid like "%${param14}%"
and jc.company_name like "%${param15}%"
and jso.fee_date >=#{param5} and jso.fee_date <=#{param6}
GROUP BY jc.company_id

SELECT jy_shiping_order.*,jy_company.*,jy_create_agreement.* from jy_shiping_order left join jy_agreementtoorder on jy_shiping_order.shiping_order_id = jy_agreementtoorder.order_id left join jy_create_agreement ON jy_create_agreement.agreement_id=jy_agreementtoorder.agreement_id left join jy_company on jy_create_agreement.company_id = jy_company.company_id where 1=1 and jy_create_agreement.agreement_type=2 AND jy_shiping_order.change_write_off!=3 AND jy_shiping_order.change_recheck_state !=2 AND jy_create_agreement.company_id in
#{item}
GROUP BY jy_shiping_order.shiping_order_id

