package com.jy.model;

public class RemarkType {
	private String remark_type_id;
	private String remark_type_name;
	
	public RemarkType() {
		super();
		// TODO Auto-generated constructor stub
	}
	public RemarkType(String remark_type_id, String remark_type_name) {
		super();
		this.remark_type_id = remark_type_id;
		this.remark_type_name = remark_type_name;
	}
	public String getRemark_type_id() {
		return remark_type_id;
	}
	public void setRemark_type_id(String remark_type_id) {
		this.remark_type_id = remark_type_id;
	}
	public String getRemark_type_name() {
		return remark_type_name;
	}
	public void setRemark_type_name(String remark_type_name) {
		this.remark_type_name = remark_type_name;
	}
	
	
}
